import React, { useEffect, useState } from "react";
import ItemForm from ".ItemForm";
import "./Item.css";

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [editId, setEditId] = useState(null);
  const [editValue, setEditValue] = useState("");

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await fetch("http://localhost:3005/api/items");
      const data = await res.json();
      setItems(data);
    } catch (error) {
      console.error("Failed to fetch items:", error);
    }
  };

  const addItem = async (item) => {
    try {
      await fetch("http://localhost:3005/api/items", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(item),
      });
      fetchItems();
    } catch (error) {
      console.error("Add item failed:", error);
    }
  };

  const deleteItem = async (id) => {
    try {
      await fetch(`http://localhost:3005/api/items/${id}`, {
        method: "DELETE",
      });
      fetchItems();
    } catch (error) {
      console.error("Delete item failed:", error);
    }
  };

  const startEditing = (item) => {
    setEditId(item._id);
    setEditValue(item.name);
  };

  const cancelEditing = () => {
    setEditId(null);
    setEditValue("");
  };

  const updateItem = async (id) => {
    try {
      await fetch(`http://localhost:3005/api/items/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: editValue }),
      });
      cancelEditing();
      fetchItems();
    } catch (error) {
      console.error("Update failed:", error);
    }
  };

  return (
    <div className="container">
      <h2 className="title">Your Items</h2>
      <ItemForm onAdd={addItem} />
      <ul className="list">
        {items.map((item) => (
          <li key={item._id} className="list-item">
            {editId === item._id ? (
              <>
                <input
                  className="input small-input"
                  value={editValue}
                  onChange={(e) => setEditValue(e.target.value)}
                />
                <button onClick={() => updateItem(item._id)} className="btn update-btn">
                  Save
                </button>
                <button onClick={cancelEditing} className="btn delete-btn">
                  Cancel
                </button>
              </>
            ) : (
              <>
                <span>{item.name}</span>
                <div>
                  <button onClick={() => startEditing(item)} className="btn edit-btn">
                    Edit
                  </button>
                  <button onClick={() => deleteItem(item._id)} className="btn delete-btn">
                    Delete
                  </button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemList;
